questionvariables.text:
stack_include("https://stack.mathedu.jp/sc/ky_linear_algebra.txt");
/* [全選択肢の数, 正解選択肢の数] (rb のときも2個以上可能) */
[oN, cN]:[5, 1];
/* random parameters (設定を変えて問題を複製するときに変える or 固定する部分) */
[rw, cl]:rand([[3, 4], [4, 5], [3, 5]]);
pv:rand([[1, 2], [1, 3], [1, 2, 3]]);
/*********************************** FLAGS ***********************************/
/* 以下は不要ならさわらなくてよい(modification not required if not necessary) */
/* Flags for mcq_template(before include pre template) */
/* -cb と -rb は解答変数の型(checkbox or radio button) と以下の設定の違いのみ */
__mcq_rb_cb:"cb";
/* cN:(min or max)(cN, 正答パターン数), wN:(min or max)(oN_cN, 誤答パターン数) */
__mcq_min_max:min;
/* feedback を表示するか? (display feedback ? ) */
__mcq_fbp:true;
/* 部分点をつけるか? (use subscore ? cf. ATCM2022 ) */
__mcq_subsc:true;
/* "わからない"選択肢を含めるか? (false) */
__mcq_noidea:false;
/* "選択肢に正解はない"選択肢を含めるか? (false) */
__mcq_nocorrectopt:false;
/* 選択肢に正解はない選択肢は正解か? (__mcq_nocorrectopt と自動で and をとる) */
__mcq_nocorrecttrue:rand([true, false]);
 /* 自動で"1つ選べ", "すべて選べ" を付加するか? (true) */
__mcq_qtext_auto:true;
/****************************** DO NOT EDIT HERE *****************************/
stack_include("https://stack.mathedu.jp/sc/mcq_template_pre.txt");
/*****************************************************************************/

/**************** QUESTION VARIABLES for MAIN CONTENT ****************/
/* 正答・誤答パターン生成用変数, 問題文生成 */
matF:randechelon(rw, cl, pv, [-2, -1, -1, 0, 1, 1, 2]);
matP:random_matrix_det_list(rw, [-2, -1, -1, 0, 1, 1, 2], [-1, 1]);
matA:matP.matF;
/* qtext : 問題文の主文(Main sentence for question text/"選べ"は自動) */
qtext:sconcat("\\(A=\\)", tex2(matA), "のとき, 行簡約形として正しいものを");
/************************ Patterns ********************/
/* 正解/誤答のパターンとフィードバックをセットで List に push していく. */
addCopt(_rp(["正解パターン1-1"]), "正答パターン1を選択しています. そのような考え方もありますね!");
addCopt(_rp(["正解パターン2-1"]), "正答パターン2を選択しています. そのような考え方もありますね!");
addCopt(_rp(["正解パターン3-1","正解パターン3-2"]), "標準的ではないですが, 正解です.");
addWopt(_rp(["誤答パターン1-1"]), "Feedback message for wrong pattern 1");
addWopt(_rp(["誤答パターン2-1"]), "Feedback message for wrong pattern 2");
addWopt(_rp(["誤答パターン3-1","誤答パターン3-2"]), "Feedback message for wrong pattern 3");
addWopt(_rp(["誤答パターン4-1","誤答パターン4-2"]), "Feedback message for wrong pattern 4");
/* 各パターンからは1つ以上選択される. 何個ずつ選ぶかは自動で行う(mkPattern関数). */
/**************** END OF QUESTION VARIABLES for MAIN CONTENT ******************/

/****************************** DO NOT EDIT HERE *****************************/
stack_include("https://stack.mathedu.jp/sc/mcq_template_post.txt");
/*****************************************************************************/

feedbackvariables.text:
/* ja:ラジオボタンの場合は, ans1 は数値， チェックボックスのときは, リスト */
/* en:radio button case: ans1 is numerical , checkbox case: ans1 is list */
if listp(ans1) then chk1:is(ans1=Cans1) else chk1:member(ans1,Cans1);
if listp(ans1) then ans1L:ans1 else ans1L:[ans1];
ans1WL:makelist(floor(ans1L[k]/_cw_delimiter),k,1,length(ans1L));
ans1CL:makelist(if ans1L[k]<_cw_delimiter then floor(ans1L[k]/_max_opts) else -1,k,1,length(ans1L));
countL(L,t):=block([k,rt:0],for k:1 thru length(L) do if L[k]=t then rt:rt+1,return(rt));
cAN:countL(ans1WL,0);
wAN:apply("+",makelist(countL(ans1WL,k),k,1,_max_patterns));
flgCL:makelist(member(k,ans1CL),k,1,length(CLbase));
flgWL:makelist(member(k,ans1WL),k,1,length(WLbase));
flgWnc:member(_nocorrect_flag,ans1WL);
flgC0:member(0,ans1CL);
flgNOID:member(_noidea_flag,ans1WL);
msg:"";
/* customize appropriate message for each incorrect(wrong) pattern */
for k:1 thru length(CLbase) do if flgCL[k] then msg:sconcat(msg,CmsgL[k],"<br>");
for k:1 thru length(WLbase) do if flgWL[k] then msg:sconcat(msg,WmsgL[k],"<br>");
if flgC0 then msg:sconcat(msg,"選択肢に正解はありません. 他を選択していなければ正解です.<br>");
if flgWnc then msg:sconcat(msg,"正解の選択肢はあります!<br>");
sc:cAN/(cN+wAN)*ssc;
/* 選択肢に正解がある(nocorrecttrue:false)のに, 正解がないを選択した場合(flg9) は, 常に 0 点 */
if (not nocorrecttrue) and flgWnc then sc:0;
/* 選択肢に正解がない時, 正解がないを選択しなかった場合は, cAN=0 なので自動的に 0点 */
/* 選択肢に正解がない時, 他の誤答選択肢を選択した場合は 0点 */
if nocorrecttrue and apply("or", flgWL) then sc:0;
if not fbp then msg:"";
